import React from "react";

import CheckoutProduct from "../checkoutProduct/checkoutProduct.component";
import "./buyDetails.styles.css";

class BuyDetails extends React.Component {
  constructor(props) {
    super(props);
    this.props = props;
    this.state = {
      active: 0,
      amount: {
        price: 0,
        count: 0,
      },
      mode: "add",
    };
    this.toggleView = this.toggleView.bind(this);
    this.getView = this.getView.bind(this);
  }

  toggleView(e) {
    if (e.target.id === "addbtn") {
      this.setState(
        {
          mode: "added",
          active: 1,
          amount: { price: this.props.price, count: 1 },
        },
        () => {}
      );
    }
    if (e.target.id === "minus" && this.state.amount.count === 1) {
      this.setState({
        mode: "add",
        active: 1,
        amount: { price: this.props.price, count: 0 },
      });
    }
    if (e.target.id === "plus") {
      this.setState({
        mode: "added",
        active: 1,
        amount: { count: this.state.amount.count + 1 },
      });
    }
    if (e.target.id === "minus" && this.state.amount.count !== 1) {
      this.setState({
        mode: "added",
        active: 1,
        amount: { count: this.state.amount.count - 1 },
      });
    }
  }

  inputChangedHandler = (event) => {
    const updatedKeyword = event.target.value;
  };

  getView(mode) {
    const views = {
      add: (
        <button id={`addbtn`} className="buy-details">
          ADD TO CART
        </button>
      ),
      added: (
        <div className="plus-minus">
          <button id={`minus`}>-</button>
          <input
            type="text"
            size="1"
            maxLength="2"
            value={this.state.amount.count}
            onChange={(event) => this.inputChangedHandler(event)}
          />
          <button id={`plus`}>+</button>
        </div>
      ),
    };
    return views[mode];
  }

  render() {
    return (
      <div onClick={this.toggleView}>
        {this.getView(this.state.mode)}{" "}
        <CheckoutProduct
          count={this.state.amount.count}
          price={this.state.amount.price}
        />
      </div>
    );
  }
}

export default BuyDetails;
