import React from "react";
import { connect } from "react-redux";

import { updateProductCount } from "../../redux/product/product.actions";
import "./buyDetails.styles.css";

class BuyDetails extends React.Component {
  toggleView(e) {
    const { updateProductCount } = this.props;
    // console.log(this.props.price);
    if (e.target.id === "addbtn") {
      updateProductCount({
        mode: "added",
        active: 1,
        price: this.props.price,
        count: 1,
      });
    }
    if (e.target.id === "minus" && this.state.count === 1) {
      updateProductCount({
        mode: "add",
        active: 1,
        price: this.props.price,
        count: 0,
      });
    }
    if (e.target.id === "plus") {
      updateProductCount({
        mode: "added",
        active: 1,
        count: this.state.count + 1,
        price: this.props.price,
      });
    }
    if (e.target.id === "minus" && this.state.count !== 1) {
      updateProductCount({
        mode: "added",
        active: 1,
        count: this.state.count - 1,
        price: this.props.price,
      });
    }
  }

  inputChangedHandler = (event) => {};

  getView(mode) {
    const views = {
      add: (
        <button id={`addbtn`} className="buy-details">
          ADD TO CART
        </button>
      ),
      added: (
        <div className="plus-minus">
          <button id={`minus`}>-</button>
          <input
            type="text"
            size="1"
            maxLength="2"
            value={updateProductCount.count}
            onChange={(event) => this.inputChangedHandler(event)}
          />
          <button id={`plus`}>+</button>
        </div>
      ),
    };
    return views[mode];
  }

  render() {
    return (
      <div onClick={this.toggleView}>
        {this.getView(updateProductCount.mode)}
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  updateProductCount: (product) => dispatch(updateProductCount(product)),
});

const mapStateToProps = (state) => ({
  count: state.product.count,
  mode: state.product.mode,
  active: state.product.active,
  price: state.product.price,
});

export default connect(mapStateToProps, mapDispatchToProps)(BuyDetails);
