import React from "react";

import "./buyDetails.styles.css";

class BuyDetails extends React.Component {
  constructor() {
    super();
    this.state = {
      active: 0,
      count: 0,
      mode: "add",
    };
    this.toggleView = this.toggleView.bind(this);
  }

  toggleView() {
    if (this.state.active) {
      this.setState({ mode: "added", active: 0 });
    } else {
      this.setState({ mode: "add", active: 1 });
    }
  }

  getView(mode) {
    // goBack(() => {
    //   this.setState({ mode: "add" });
    // });
    const views = {
      add: <button className="buy-details">ADD TO CART</button>,
      added: (
        <div>
          <button>-</button>
          <input type="text" />
          <button>+</button>
        </div>
      ),
    };
    return views[mode];
  }

  render() {
    return <div onClick={this.toggleView}>{this.getView(this.state.mode)}</div>;
  }
}

export default BuyDetails;
