import React from "react";

import "./buyDetails.styles.css";

class BuyDetails extends React.Component {
  constructor(props) {
    super(props);
    this.props = props;
    this.state = {
      active: 0,
      count: 0,
      mode: "add",
    };
    this.toggleView = this.toggleView.bind(this);
    this.getView = this.getView.bind(this);
  }

  toggleView(e) {
    console.log(e.target.id);
    if (e.target.id) {
    }
    if (this.state.active) {
      this.setState({ mode: "add", active: 0 });
    } else {
      this.setState({ mode: "added", active: 1 });
    }
  }

  getView(mode) {
    const views = {
      add: (
        <button id={`"addbtn-"${this.props.price}`} className="buy-details">
          ADD TO CART
        </button>
      ),
      added: (
        <div className="plus-minus">
          <button amount={this.props.price} id="minus">
            -
          </button>
          <input type="text" size="1" maxLength="2" />
          <button amount={this.props.price} id="plus">
            +
          </button>
        </div>
      ),
    };
    return views[mode];
  }

  render() {
    return <div onClick={this.toggleView}>{this.getView(this.state.mode)}</div>;
  }
}

export default BuyDetails;
