import React from "react";

import "./buyDetails.styles.css";

class BuyDetails extends React.Component {
  constructor() {
    super();
    this.state = {
      active: 0,
      count: 0,
      mode: "add",
    };
    this.toggleView = this.toggleView.bind(this);
    this.goBack = this.goBack.bind(this);
    this.getView = this.getView.bind(this);
  }

  goBack() {
    this.setState({ mode: "add" });
  }

  toggleView() {
    if (this.state.active) {
      this.setState({ mode: "added" });
    } else {
      this.setState({ mode: "add" });
    }
  }

  getView(mode) {
    // goBack(() => {
    //   this.setState({ mode: "add" });
    // });
    const views = {
      add: <button className="buy-details">ADD TO CART</button>,
      added: (
        <div>
          <button onClick={goBack}>-</button>
          <input type="text" />
          <button>+</button>
        </div>
      ),
    };
    return views[mode];
  }

  render() {
    return <div onClick={this.toggleView}>{this.getView(this.state.mode)}</div>;
  }
}

export default BuyDetails;
