import React from "react";

import "./buyDetails.styles.css";

class BuyDetails extends React.Component {
  constructor() {
    super();
    this.state = {
      active: 0,
      count: 0,
      mode: "add",
    };
  }

  addToCart(e) {
    this.setState.count = +1;
  }

  getView(mode) {
    const views = {
      add: (
        <button
          onClick={this.setState({ mode: "added" })}
          className="buy-details"
        >
          ADD TO CART
        </button>
      ),
      added: <div>Hello</div>,
    };
    return views[mode];
  }

  render() {
    return <div>{this.getView(this.state.mode)}</div>;
  }
}

