const INITIAL_STATE = {
  active: 0,
  price: 0,
  count: 0,
  mode: "add",
};

const productReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "UPDATE_PRODUCT_COUNT":
      return {
        ...state,
        count: action.payload,
      };
    default:
      return state;
  }
};

export default productReducer;
