import React from "react";

import ProductItems from "../productItems/productItems.component";
import "./productDirectory.styles.css";

class ProductDirectory extends React.Component {
  constructor() {
    super();
    this.state = {
      productList: [
        {
          id: 1,
          name: "Managed Security Services",
          price: "$2549.99",
        },
        {
          id: 2,
          name: "Engineering & integration",
          price: "$1025.50",
        },
        {
          id: 3,
          name: "Training",
          price: "$100",
        },
      ],
    };
  }

  render() {
    return (
      <div className="product-directory">
        {this.state.productList.map(({ id, name, price }) => {
          return (
            <ProductItems key={id} dataKey={id} name={name} price={price} />
          );
        })}
      </div>
    );
  }
}

export default ProductDirectory;
